tinymce.PluginManager.add('incut', function(editor,url) {

    var TYPE_1 = 1;
    var TYPE_2 = 2;

    function makeIncut(type) {
        var selection = editor.selection;
        var content = selection.getContent();
        if(!content.length)
            return;
        var incut = document.createElement('div');
        incut.className = type == TYPE_1 ? 'incut' : 'incut-2';
        incut.innerHTML = content;
        selection.setContent('<br>' + incut.outerHTML + '<br>');
    }

    editor.addButton('incut', {
        type: 'button',
        text: 'Выделить врезку',
        icon: false,
        onclick: function() {
            makeIncut(TYPE_2);
        },
    });

    /*editor.addButton('incut2', {
        type: 'button',
        text: 'Выделить врезку (тип 2)',
        icon: false,
        onclick: function() {
            makeIncut(TYPE_2);
        },
    });*/

});