tinymce.PluginManager.add('insertAction', function(editor,url) {

    function insertAction() {
        var selection = editor.selection;
        var wrap = document.createElement('div');
        wrap.className = 'actbutton';
        wrap.innerText = '[[~BUTTON~]]';
        selection.setContent('<br>' + wrap.outerHTML + '<br>');
    }

    editor.addButton('insertAction', {
        type: 'button',
        text: 'Вставить кнопку связи',
        icon: false,
        onclick: insertAction,
    });
});