tinymce.PluginManager.add('insertGallery', function(editor,url) {

    function insertGallery() {
        var selection = editor.selection;
        var wrap = document.createElement('div');
        wrap.innerText = '[[~GALLERY~]]';
        selection.setContent(wrap.outerHTML);
    }

    editor.addButton('insertGallery', {
        type: 'button',
        text: 'Вставить галерею',
        icon: false,
        onclick: insertGallery,
    });
});